import React, { useState } from 'react';

function DetailIngredients() {
  const [detail] = useState('');
  const ingredients = [];
  const max = 20;

  for (let index = 1; index <= max; index += 1) {
    ingredient = `strIngredient${index}`;
    if (detail.ingredient !== null) {
      ingredients.push(detail.ingredient);
    }
  }

  return (
    <>
      {ingredients.map((ingredient) => (
        <p key={ ingredient }>
          -
          { ingredient.value }
        </p>))}
    </>
  );
}

export default DetailIngredients;
