import React, { useCallback, useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const max = 20;

function InProgress() {
  const [detail, setDetail] = useState('');
  const idReceita = useParams();

  const foodDetail = useCallback(async () => {
    const url = 'https://www.themealdb.com/api/json/v1/1/lookup.php?i=';
    const { meals } = await fetch(`${url}${idReceita.idReceita}`)
      .then((response) => response.json());
    setDetail(meals[0]);
  }, [idReceita]);

  useEffect(() => {
    foodDetail();
  }, [foodDetail]);

  const ingredients = [];
  const ingredientsStorage = [];
  let storage = {};

  for (let index = 1; index <= max && detail; index += 1) {
    console.log(detail, 'xablau');
    if (detail[`strIngredient${index}`]) {
      ingredients.push(
        `${detail[`strIngredient${index}`]} - ${detail[`strMeasure${index}`]}`,
      );
      ingredientsStorage.push(
        `${detail[`strIngredient${index}`]} - ${detail[`strMeasure${index}`]}`,
      );
    }
  }

  storage = {
    meals: {
      [idReceita.idReceita]: ingredientsStorage,
    },
  };

  function check(ingredient) {
    if (storage.meals[idReceita.idReceita][ingredient]) {
      storage.meals[idReceita.idReceita][ingredient] = false;
    } else {
      storage.meals[idReceita.idReceita][ingredient] = true;
    }
    return true;
  }

  console.log(storage);

  return (
    <>
      { detail && (
        <>
          <header>
            <img
              src={ detail.strMealThumb }
              alt="food-icon"
              data-testid="recipe-photo"
            />
          </header>
          <main>
            <div>
              <h1 data-testid="recipe-title">
                { detail.strMeal }
              </h1>
              <button type="button" data-testid="share-btn">
                <img
                  src=""
                  alt="share-icon"
                />
                Compartilhar
              </button>
              <button type="button" data-testid="favorite-btn">
                Favorito
              </button>
              <p data-testid="recipe-category">
                { detail.strCategory }
              </p>
            </div>
            <h2> Ingredientes </h2>
            {ingredients.map((ingredient) => (
              <div
                data-testid={
                  `${ingredients.indexOf(ingredient)}-ingredient-step`
                }
                key={ ingredients.indexOf(ingredient) }
              >
                <input type="checkbox" id={ ingredient } onChange={ check(ingredient) } />
                <label htmlFor={ ingredient }>
                  -
                  { ingredient }
                </label>
              </div>))}
            <h2> Istructions </h2>
            <div data-testid="instructions">
              { detail.strInstructions }
            </div>
          </main>
          <button type="button" data-testid="finish-recipe-btn"> Finish Recipe </button>
        </>
      ) }
      { localStorage.setItem('inProgressRecipes', storage) }

    </>
  );
}

export default InProgress;
