import React from 'react';
import { BrowserRouter as useParams } from 'react-router-dom';

function FoodDetails() {
  const { idReceita } = useParams();
  const foodDetail = useCallback(async () => {
    const url = 'https://www.themealdb.com/api/json/v1/1/lookup.php?i=';
    const { meals } = await fetch(`${url}${idReceita}`)
      .then((response) => response.json());
    setDetail(meals);
  }, [idReceita]);

  // requisicao API de bebidas
  const drinkDetail = useCallback(async () => {
    const url = 'https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=';
    const { drinks } = await fetch(`${url}${idReceita}`)
      .then((response) => response.json());
    setDetail(drinks);
  }, [idReceita]);

  useEffect(() => {
    if (foodRecipe === true) {
      foodDetail();
      console.log('ok esta funcionando food');
    } else if (apiFilter === 'Drinks') {
      drinkDetail();
      console.log('ok esta funcionando drink');
    }
  }, [foodRecipe, foodDetail, drinkDetail]);

  return (
    <>
      <header>
        <img src={ detail.strMealThumb } alt="food-icon" data-testid="recipe-photo" />
      </header>
      <main>
        <div>
          <h1 data-testid="recipe-title"> Titulo Rrceita </h1>
          <button type="button" data-testid="share-btn">
            <img
              src=""
              alt="share-icon"
            />
            Compartilhar
          </button>
          <button type="button" data-testid="favorite-btn">
            Favorito
          </button>
          <p data-testid="recipe-category"> Categoria </p>
        </div>
        <h2> Ingredientes </h2>
        <div data-testid={ `${index}-ingredient-name-and-measure` }>
          Ingredients
        </div>
        <h2> Istructions </h2>
        <div data-testid="instructions">
          Instructions
        </div>
        { foodRecipe
          ? (
            <>
              <h2> Video </h2>
              <div>
                <source src="/Videos/video1.mp4" data-testid="video" type="video/mp4" />
              </div>
            </>
          ) : null}
        <h2> Recommended </h2>
        <div data-testid={ `${index}-recomendation-card` }>
          Ingredients
        </div>
      </main>
      <button type="button" data-testid="start-recipe-btn"> Start Recipe </button>
    </>
  );
}

export default FoodDetails;
