import React, { useCallback, useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const max = 20;

function DrinkInProgress() {
  const [detail, setDetail] = useState('');
  const idReceita = useParams();
  const drinkObject = JSON.parse(localStorage.getItem(('inProgressRecipes')));

  const drinkDetail = useCallback(async () => {
    const url = 'https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=';
    const { drinks } = await fetch(`${url}${idReceita.idReceita}`)
      .then((response) => response.json());
    setDetail(drinks[0]);
  }, [idReceita]);

  useEffect(() => {
    drinkDetail();
  }, [drinkDetail]);

  const ingredients = [];
  const ingredientsStorage = [];

  for (let index = 1; index <= max && detail; index += 1) {
    if (detail[`strIngredient${index}`]) {
      ingredients.push(
        `${detail[`strIngredient${index}`]} - ${detail[`strMeasure${index}`]}`,
      );
      ingredientsStorage[
        `${detail[`strIngredient${index}`]} - ${detail[`strMeasure${index}`]}`] = '';
    }
  }

  const storage = {
    cocktails: {
      [idReceita.idReceita]: { ...ingredientsStorage },
    },
    meals: {},
  };
  if (drinkObject === null) {
    localStorage.setItem('inProgressRecipes', JSON.stringify(storage));
  }

  const check = () => {
    for (let index = 0;
      index < Object.keys(ingredientsStorage).length && detail; index += 1) {
      const box = document.getElementById(Object.keys(ingredientsStorage)[index]);
      if (box.checked) {
        storage.cocktails[
          idReceita.idReceita][Object.keys(ingredientsStorage)[index]] = true;
        localStorage.setItem('inProgressRecipes', JSON.stringify(storage));
      } else {
        storage.cocktails[
          idReceita.idReceita][Object.keys(ingredientsStorage)[index]] = false;
        localStorage.setItem('inProgressRecipes', JSON.stringify(storage));
      }
    }
  };

  return (
    <>
      { detail && (
        <>
          <header>
            <img
              src={ detail.strDrinkThumb }
              alt="food-icon"
              data-testid="recipe-photo"
            />
          </header>
          <main>
            <div>
              <h1 data-testid="recipe-title">
                { detail.strDrink }
              </h1>
              <button type="button" data-testid="share-btn">
                <img
                  src=""
                  alt="share-icon"
                />
                Compartilhar
              </button>
              <button type="button" data-testid="favorite-btn">
                Favorito
              </button>
              <p data-testid="recipe-category">
                { detail.strAlcoholic }
              </p>
            </div>
            <h2> Ingredientes </h2>
            {ingredients.map((ingredient) => (
              <div
                data-testid={
                  `${ingredients.indexOf(ingredient)}-ingredient-step`
                }
                key={ ingredients.indexOf(ingredient) }
              >
                <input
                  type="checkbox"
                  id={ ingredient }
                  onChange={ () => check() }
                  defaultChecked={
                    drinkObject.cocktails[idReceita.idReceita][ingredient]
                  }
                />
                <label htmlFor={ ingredient }>
                  -
                  { ingredient }
                </label>
              </div>))}
            <h2> Istructions </h2>
            <div data-testid="instructions">
              { detail.strInstructions }
            </div>
          </main>
          <button type="button" data-testid="finish-recipe-btn"> Finish Recipe </button>
        </>
      )}
      { localStorage.setItem('inProgressRecipes', storage) }
    </>
  );
}

export default DrinkInProgress;
