import React, { useCallback, useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function FoodDetails() {
  const [detail, setDetail] = useState('');
  const idReceita = useParams();
  const historyUrl = window.location.href;
  const foodRecipe = historyUrl.includes('food');

  const foodDetail = useCallback(async () => {
    const url = 'https://www.themealdb.com/api/json/v1/1/lookup.php?i=';
    const { meals } = await fetch(`${url}${idReceita.idReceita}`)
      .then((response) => response.json());
    setDetail(meals[0]);
  }, [idReceita]);

  const drinkDetail = useCallback(async () => {
    const url = 'https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=';
    const { drinks } = await fetch(`${url}${idReceita.idReceita}`)
      .then((response) => response.json());
    setDetail(drinks[0]);
  }, [idReceita]);

  useEffect(() => {
    if (foodRecipe) {
      foodDetail();
      console.log('ok esta funcionando food');
    } else {
      drinkDetail();
      console.log('ok esta funcionando drink');
    }
  }, [foodRecipe, foodDetail, drinkDetail]);

  const max = 20;
  const ingredients = [];
  for (let index = 1; index <= max; index += 1) {
    if (detail[`strIngredient${index}`]) {
      ingredients.push(detail[`strIngredient${index}`]);
    }
  }

  return (
    <>
      {' '}
      { detail ? (
        <>
          <header>
            <img
              src={ foodRecipe ? (detail.strMealThumb) : detail.strDrinkThumb }
              alt="food-icon"
              data-testid="recipe-photo"
            />
          </header>
          <main>
            <div>
              <h1 data-testid="recipe-title">
                {foodRecipe
                  ? (
                    detail.strMeal
                  ) : detail.strDrink }
              </h1>
              <button type="button" data-testid="share-btn">
                <img
                  src=""
                  alt="share-icon"
                />
                Compartilhar
              </button>
              <button type="button" data-testid="favorite-btn">
                Favorito
              </button>
              <p data-testid="recipe-category">
                { detail.strCategory }
              </p>
            </div>
            <h2> Ingredientes </h2>
            <div data-testid={ `${idReceita}-ingredient-name-and-measure` }>
              {ingredients.map((ingredient) => (
                <p key={ ingredient }>
                  -
                  { ingredient }
                </p>))}
            </div>
            <h2> Istructions </h2>
            <div data-testid="instructions">
              { detail.strInstructions }
            </div>
            { foodRecipe
              ? (
                <>
                  <h2> Video </h2>
                  <video width="320" height="240" controls>
                    <source
                      src={ detail.strYoutube }
                      data-testid="video"
                      type="video/mp4"
                    />
                    <track src="" kind="captions" srcLang="en" label="english_captions" />
                  </video>
                </>
              ) : null}
            <h2> Recommended </h2>
            <div data-testid={ `${idReceita}-recomendation-card` }>
              Ingredients
            </div>
          </main>
          <button type="button" data-testid="start-recipe-btn"> Start Recipe </button>
        </>
      ) : <h1> Loading... </h1> }
    </>
  );
}

export default FoodDetails;
